#['ffmpeg', '-i', 'output/TED.mp4', '-vf', b"scale=640:360:force_original_aspect_ratio=decrease,pad=640:360:(ow-iw)/2:(oh-ih)/2,subtitles=output/src.srt:force_style='FontSize=15,FontName=NotoSansCJK-Regular,PrimaryColour=&HFFFFFF,OutlineColour=&H000000,OutlineWidth=1,ShadowColour=&H80000000,BorderStyle=1',subtitles=output/trans.srt:force_style='FontSize=17,FontName=NotoSansCJK-Regular,PrimaryColour=&H00FFFF,OutlineColour=&H000000,OutlineWidth=1,BackColour=&H33000000,Alignment=2,MarginV=27,BorderStyle=4'", '-c:v', 'h264_nvenc', '-y', 'output/output_sub.mp4']
